def process_page(browser, exec, city_id, cur_city)

	
	# 6  Finds all of the "zD.areas" variables in the html
	#
	matches = browser.html.scan(/zD.areas\[[0-9]+\].*?\};/m)
	region = nil
	regionid = nil
	lat = nil
	lng = nil
	points = nil
	area = nil
  index_value = {'5'=>0,'4.5'=>1,'4'=>2,'3.5'=>3,'3'=>4,'2.5'=>5,'2'=>6,'1'=>7}
	star_availability = {}
	# 7  Process each variable and output its value
	#
	output = ""

  # 7a  Get area if any for the current city
  #

  if not browser.element_by_xpath("//*[@id='map_tbl']/tbody/tr/td[1]/div/span/b|//*[contains(@class , 'alert_msg')]").nil?
    area = browser.element_by_xpath("//*[@id='map_tbl']/tbody/tr/td[1]/div/span/b|//*[contains(@class , 'alert_msg')]").innerText
    puts area
  end

  # Check if area is already in the db, if it is connect the current city to the area and dont process any further
  #
  if area then
    if check_area(area,city_id) then
      return
    end
  end

  # 7b  Get star_avalability for each region
  #
  if not browser.element_by_xpath(".//tr[contains(@class,'zone')]").nil?
    
    # 7b i  iterate through all the region checkboxes
    #

    browser.checkboxes.each do |zone|
      #puts zone.value
      zone.click

      # 7b ii  iterate through all the available star ratings for the region
      #
      bit_string = [0,0,0,0,0,0,0,0]

      # 5 4.5 4 3.5 3 2.5 2 1 => 4.5 being resorts
      # corrosponds to bits in the bit string, the most significant bit corrosponding to 5

      browser.elements_by_xpath(".//table[contains(@class,'star')]/tbody/tr/td[2]/a[not(contains(@class,'na'))]").each { |e|  bit_string[index_value[e.id[1..-3]]] = 1 }
      zone.click

      star_availability[zone.value] = ""

      bit_string.each { |item|  star_availability[zone.value]+=item.to_s }

    end
    
  end

	matches.each_with_index do |match, index|
	
		# 7c  On even indices set region names and region id's
		#
		if index % 2 == 0 then
			if match =~ /(name:")(.*?)(")/ then
				region = $2
			end
			if match =~ /(id:")(.*?)(")/ then
				regionid = $2
			end
			
		# 7d  On odd indices decode the co-ordinates
		#
		else
			if match =~ /(lat:")([0-9.]*?)(")/ then
				lat = $2
			end
			if match =~ /(lng:")([-0-9.]*?)(")/ then
				lng = $2
			end
			if match =~ /(points:")(.*?)(")/ then
				points = $2
			end
			
			if (region and regionid and lat and lng and points) then
				
				# 7e  Execute the javascript online
				#
				exec.text_field(:id, "textarea-for-code").value = "function veDecodeLine(a,c,d){var e=a.replace(/0/g,\"\\\\\");var f=e.length;var g=0;var h=[];var i=0;var j=0;var k;try{while(g<f){var b;var l=0;var m=0;do{b=e.charCodeAt(g++)-63;m|=(b&0x1f)<<l;l+=5}while(b>=0x20);var n=((m&1)?~(m>>1):(m>>1));i+=n;l=0;m=0;do{b=e.charCodeAt(g++)-63;m|=(b&0x1f)<<l;l+=5}while(b>=0x20);var o=((m&1)?~(m>>1):(m>>1));j+=o;document.write(c+\";\"+d+\";\"+parseFloat(i*1e-5,10)+\";\"+parseFloat(j*1e-5,10)+\"$\")}}catch(ex){}}veDecodeLine(\"#{points}\", \"#{region}\", \"#{city_id}\");"
				exec.link(:class, "button-run").click
				points = exec.div(:id, "code-output").text
				File.open("pl.citytoregion", "a") { |f| f.write("#{cur_city} HAS #{region}\n") }
				
				# 7f  Output the centre point, then a list of its boundaries
				#

				if area then
          output += "##{area};"
        else
          output += "#"
        end


        output += "#{region};#{regionid};#{star_availability[regionid]};#{city_id};#{lat};#{lng}$"
				output += points
				
       
				sleep 0.5
			end
			
			# 7g  Reset variables for next loop
			#
			region = nil
			lat = nil
			lng = nil
			points = nil
		end
		
		
	end
	
	if output == "" then
		raise NoResults
	end
	puts output
	File.open("output/process.txt", "w") { |file| file.write(output) }
	`python process.py`
	unless $?.success?
		raise ProcessingFailure
	end

end

